mapper.select:
select no, writer, main_category, middle_category, title, content, view_cnt, to_char(reg_date, 'yyyy-mm-dd') as reg_date,
	admin_name, empno, consulting_channel, reply_cnt
	from inquiry_tb  where no = #{no}

mapper.update:

update inquiry_tb
	set reply_cnt = reply_cnt + 1
  where no = #{no}


update inquiry_tb
	set reply_cnt = reply_cnt - 1
  where no = #{no}

update inquiry_tb set
view_cnt = view_cnt + 1
 where no = #{no}
